import { useEffect, useMemo, useState } from "react";
import { Report } from "interface/report.interface";
import FieldList from "./FieldList"; // Importar o novo componente
import { ReportPage } from "components/ReportPage";
import "../../styles/pdf.css";

export type Field = {
    id?: string;
    type: "Text" | "Number" | "Date" | "Label" | "CheckBox" | "Line" | string;
    name: string;
    value?: string | number | Date | boolean;
    placeholder?: string;
    required?: boolean;
    order?: number;
};

export interface TemplatePreviewProps {
    onSave: (report: Report) => void;
    fields?: Field[];
}

const TemplatePreview: React.FC<TemplatePreviewProps> = ({ onSave, fields: initialFields }) => {
    const [layout, setLayout] = useState<Report["body"]["layout"]>("RIGHT");
    const [imageCount, setImageCount] = useState(6);

    console.log("INICIAL VALUES");
    console.log(initialFields);

    const baseImage = useMemo(() => ({
        url: "data:image/jpeg;base64,",
        altText: "Imagem do Exame",
        caption: "Legenda Exemplo",
    }), []);

    const [fields, setFields] = useState<Field[]>(initialFields ?? []);
    const [report, setReport] = useState<Report>({
        header: null as any,
        body: { 
            layout: layout, 
            fields: fields as any, // Type assertion to handle the type mismatch
            images: Array(imageCount).fill(baseImage) 
        },
        footer: null as any,
    });

    const [editingField, setEditingField] = useState<string | null>(null);
    const [newField, setNewField] = useState<{ name: string; type: Field["type"] }>({
        name: "",
        type: "Text",
    });

    // Função para gerar ID único para campos
    const generateId = () => {
        return Math.random().toString(36).substring(2, 9);
    };

    const handleAddField = () => {
        if (newField.name) {
            if (editingField) {
                // Update existing field
                setFields((prevFields) =>
                    prevFields.map((field) =>
                        field.id === editingField
                            ? { ...field, name: newField.name, type: newField.type }
                            : field
                    )
                );
                setEditingField(null);
            } else {
                // Add new field
                const field: Field = {
                    ...newField,
                    id: generateId(),
                    required: false,
                    order: fields.length, // Atribuir ordem baseada no número de campos
                };
                setFields((prevFields) => [...prevFields, field]);
            }
            setNewField({ name: "", type: "Text" });
        }
    };
    
    const moveFieldUp = (id: string) => {
        setFields(prevFields => {
            const index = prevFields.findIndex(field => field.id === id);
            if (index <= 0) return prevFields; // Já está no topo, não faz nada
            
            const newFields = [...prevFields];
            // Troca o campo atual com o campo acima
            const temp = newFields[index];
            newFields[index] = newFields[index - 1];
            newFields[index - 1] = temp;
            
            // Atualiza as ordens
            return newFields.map((field, idx) => ({
                ...field,
                order: idx
            }));
        });
    };
    
    const moveFieldDown = (id: string) => {
        setFields(prevFields => {
            const index = prevFields.findIndex(field => field.id === id);
            if (index === -1 || index >= prevFields.length - 1) return prevFields; // Já está no fim, não faz nada
            
            const newFields = [...prevFields];
            // Troca o campo atual com o campo abaixo
            const temp = newFields[index];
            newFields[index] = newFields[index + 1];
            newFields[index + 1] = temp;
            
            // Atualiza as ordens
            return newFields.map((field, idx) => ({
                ...field,
                order: idx
            }));
        });
    };

    const handleEditField = (id: string) => {
        const fieldToEdit = fields.find((field) => field.id === id);
        if (fieldToEdit) {
            setNewField({
                name: fieldToEdit.name,
                type: fieldToEdit.type,
            });
            setEditingField(id);
        }
    };

    const handleCancelEdit = () => {
        setEditingField(null);
        setNewField({ name: "", type: "Text" });
    };

    const handleRemoveField = (id: string) => {
        setFields((prevFields) => prevFields.filter((field) => field.id !== id));
        if (editingField === id) {
            handleCancelEdit();
        }
    };

    useEffect(() => {
        //@ts-ignore
        setReport((prevReport) => ({
            ...prevReport,
            body: {
                ...prevReport.body,
                layout: layout,
                images: Array(imageCount).fill(baseImage),
                fields: fields,
            },
        }));
    }, [fields, layout, imageCount, baseImage]);

    return (
        <div className="p-4 bg-gray-100 min-h-screen flex flex-row gap-6">
            {/* Área de visualização do relatório */}
            <div className="flex-1 bg-white p-6 rounded shadow">
                <div className="flex justify-between items-center mb-4">
                    <h1 className="text-2xl font-semibold">Template Preview</h1>
                    <button
                        onClick={() => onSave(report)}
                        className="px-4 py-2rounded save-button">
                        Salvar Corpo do Laudo
                    </button>
                </div>
                <div className="classA4 m-4">
                    <ReportPage report={report} isPrint={false} fieldValues={fields} updateFieldValue={null as any} isDisabled={true} />
                </div>
            </div>

            {/* Painel de configuração e adição de campo à direita */}
            <div className="w-80 bg-white p-6 rounded shadow-md flex flex-col gap-4">
                <h2 className="text-xl font-medium mb-2 text-gray-800">Configurations</h2>

                {/* Seletor de Layout */}
                <label className="block text-gray-700">Layout</label>
                <select
                    value={layout}
                    onChange={(e) => setLayout(e.target.value as Report["body"]["layout"])}
                    className="border border-gray-300 rounded p-2 w-full focus:border-blue-500 focus:outline-none"
                >
                    <option value="RIGHT">Right</option>
                    <option value="LEFT">Left</option>
                    <option value="DOWN">Down</option>
                    <option value="UP">Up</option>
                </select>

                {/* Seletor de Quantidade de Imagens */}
                <label className="block text-gray-700">Number of Images</label>
                <input
                    type="number"
                    min="1"
                    max="5"
                    value={imageCount}
                    onChange={(e) => setImageCount(parseInt(e.target.value))}
                    className="border border-gray-300 rounded p-2 w-full focus:border-blue-500 focus:outline-none"
                    disabled={true}
                />

                <h2 className="text-xl font-medium mt-4 mb-2 text-gray-800">Add New Field</h2>
                <input
                    type="text"
                    placeholder="Field Name"
                    value={newField.name}
                    onChange={(e) => setNewField({ ...newField, name: e.target.value })}
                    className="border border-gray-300 rounded p-2 w-full focus:border-blue-500 focus:outline-none"
                />
                <select
                    value={newField.type}
                    onChange={(e) => setNewField({ ...newField, type: e.target.value as Field["type"] })}
                    className="border border-gray-300 rounded p-2 w-full focus:border-blue-500 focus:outline-none"
                >
                    <option value="Text">Text</option>
                    <option value="Label">Label</option>
                    <option value="CheckBox">Checkbox</option>
                    <option value="Line">Line</option>
                </select>
                <div className="flex space-x-2">
                    <button
                        onClick={handleAddField}
                        className="px-4 py-2 mt-4 bg-blue-600 text-white rounded hover:bg-blue-700 flex-1">
                        {editingField ? 'Update Field' : 'Add Field'}
                    </button>
                    {editingField && (
                        <button
                            onClick={handleCancelEdit}
                            className="px-4 py-2 mt-4 bg-gray-500 text-white rounded hover:bg-gray-600">
                            Cancel
                        </button>
                    )}
                </div>

                <FieldList 
                    fields={fields.sort((a, b) => (a.order || 0) - (b.order || 0))} 
                    onEditField={handleEditField} 
                    onRemoveField={handleRemoveField}
                    onMoveUp={moveFieldUp}
                    onMoveDown={moveFieldDown}
                />
            </div>
        </div>
    );
};

export default TemplatePreview;
